/** @format */

import styled from "styled-components";

export const TitleBlock = styled.div`
  display: flex;
  flex-direction: column;
  position: relative;
  height: 50vh;
`;

export const Thumbnail = styled.div`
  height: 50%;
  background: orange;
`;

export const TitleBox = styled.div`
  display: flex;
  flex-direction: column;
  background: white;
  align-items: center;
  position: absolute;
  z-index: 100;
  top: 45%;
  left: 50%;
  width: 90%;
  h2 {
    font-weight: bold;
    margin-top: 5vh;
  }
`;
