/** @format */

import React, { useCallback, useState } from "react";
import styled from "styled-components";
import { Button } from "antd";
import LoginForm from "./LoginForm";
const FisrtBlock = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 0 auto;
  max-width: 768px;
  height: 100vh;
  padding: 60px 30px;
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: space-around;
  height: 10vh;
`;

const ButtonBlock = styled(Button)`
  background-color: #42a5f5;
  color: white;
  height: 100%;
  background: ${(props) => props.background};
`;

const First = () => {
  const [custom, setcustom] = useState(false);
  const [admin, setadmin] = useState(false);
  const onCustom = useCallback(() => {
    setcustom((prev) => !prev);
  }, []);
  const onAdmin = useCallback(() => {
    setadmin((prev) => !prev);
  }, []);
  {
    if (custom) return <LoginForm onCustom={onCustom} />;
  }
  {
    if (admin && !custom) return <LoginForm admin={admin} onAdmin={onAdmin} />;
  }
  return (
    <>
      <FisrtBlock>
        <ButtonGroup>
          <ButtonBlock onClick={onCustom}>일반 로그인</ButtonBlock>
          <ButtonBlock background="#1EB045" onClick={onAdmin}>
            사장님 로그인
          </ButtonBlock>
        </ButtonGroup>
      </FisrtBlock>
    </>
  );
};

export default First;
