/** @format */

import React from "react";
import Link from "next/link";
import { Menu } from "antd";
import {
  HomeOutlined,
  SearchOutlined,
  HeartOutlined,
  CarryOutOutlined,
  UserOutlined,
} from "@ant-design/icons";
import LoginForm from "./LoginForm";
import styled from "styled-components";
import { useSelector } from "react-redux";

const MenuBlock = styled(Menu)`
  max-width: 768px;
  display: flex;
  justify-content: space-between;
  position: fixed;
  bottom: 0;
  z-index: 100;
`;

const ItemBlock = styled(Menu.Item)`
  text-align: center;
`;

// eslint-disable-next-line react/prop-types
const AppLayout = ({ children }) => {
  const { me } = useSelector((state) => state.user);
  return (
    <div>
      {me ? (
        <>
          {children}{" "}
          <MenuBlock mode="inline">
            <ItemBlock>
              <Link href="/">
                <a>
                  <HomeOutlined />
                </a>
              </Link>
            </ItemBlock>
            <ItemBlock>
              <Link href="/search">
                <a>
                  <SearchOutlined />
                </a>
              </Link>
            </ItemBlock>
            <ItemBlock>
              <Link href="/favorite">
                <a>
                  <HeartOutlined />
                </a>
              </Link>
            </ItemBlock>
            <ItemBlock>
              <Link href="/cart">
                <a>
                  <CarryOutOutlined />
                </a>
              </Link>
            </ItemBlock>
            <ItemBlock>
              <Link href="/profile">
                <a>
                  <UserOutlined />
                </a>
              </Link>
            </ItemBlock>
          </MenuBlock>
        </>
      ) : (
        <LoginForm />
      )}
    </div>
  );
};

export default AppLayout;
