/** @format */

import React, { useState, useCallback } from "react";
import Link from "next/link";
import { Menu } from "antd";
import {
  HomeOutlined,
  SearchOutlined,
  HeartOutlined,
  CarryOutOutlined,
  UserOutlined,
} from "@ant-design/icons";

// eslint-disable-next-line react/prop-types
const AppLayout = ({ children }) => {
  const [current, setCurrent] = useState("Home");

  const onSelected = useCallback((e) => {
    setCurrent(e.key);
    console.log(current);
  }, []);
  return (
    <div>
      {children}
      <Menu
        mode="inline"
        style={{ display: "flex", justifyContent: "space-between" }}
        onClick={onSelected}
        selectedKeys={[current]}
      >
        <Menu.Item key="Home">
          <Link href="/">
            <a>
              <HomeOutlined />
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item key="Search">
          <Link href="/search">
            <a>
              <SearchOutlined />
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item key="Favorite">
          <Link href="/favorite">
            <a>
              <HeartOutlined />
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item key="Cart">
          <Link href="/cart">
            <a>
              <CarryOutOutlined />
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item key="Profile">
          <Link href="/profile">
            <a>
              <UserOutlined />
            </a>
          </Link>
        </Menu.Item>
      </Menu>
    </div>
  );
};

export default AppLayout;
