/** @format */

import React, { useState, useCallback } from "react";
import Link from "next/link";
import { Menu } from "antd";
import {
  HomeOutlined,
  SearchOutlined,
  HeartOutlined,
  CarryOutOutlined,
  UserOutlined,
  HomeTwoTone,
} from "@ant-design/icons";

import styled from "styled-components";

const MenuItem = styled.a`
  text-align: center;
  color: #e2e2e2;
  &.active {
    color: black;
  }
`;

// eslint-disable-next-line react/prop-types
const AppLayout = ({ children }) => {
  const [linked, setLinked] = useState(false);
  const onToggle = useCallback(() => {
    setLinked((prev) => !prev);
  }, []);
  return (
    <div>
      {children}
      <Menu
        mode="horizontal"
        style={{ display: "flex", justifyContent: "space-between" }}
      >
        <Menu.Item>
          <Link href="/">
            <MenuItem>
              {linked ? (
                <HomeOutlined style={{ color: "#808080" }} onClick={onToggle} />
              ) : (
                <HomeTwoTone twoToneColor="black" onClick={onToggle} />
              )}
            </MenuItem>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/search">
            <a>
              <SearchOutlined />
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/favorite">
            <a>
              <HeartOutlined />
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/cart">
            <a>
              <CarryOutOutlined />
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/profile">
            <a>
              <UserOutlined />
            </a>
          </Link>
        </Menu.Item>
      </Menu>
    </div>
  );
};

export default AppLayout;
