/** @format */

import React from "react";
import styled from "styled-components";

const MenuCardBlock = styled.div`
  width: 90%;
  margin: 0 auto;
  display: flex;
  padding: 2vh 0;
`;
const MenuCardDesc = styled.div`
  flex: 2.5;
`;

const MenuCardImg = styled.div`
  flex: 1;
  width: 100px;
  height: 100px;
  background: #fff;
`;

const MenuCardDescT = styled.div`
  font-weight: bold;
  font-size: 17px;
  margin-bottom: 0.5rem;
`;

const MenuCardDescP = styled.div`
  font-size: 15px;
`;
const MenuCard = () => {
  return (
    <MenuCardBlock>
      <MenuCardDesc>
        <MenuCardDescT>순살양념구이</MenuCardDescT>
        <MenuCardDescP>20,000원</MenuCardDescP>
      </MenuCardDesc>
      <MenuCardImg />
    </MenuCardBlock>
  );
};

export default MenuCard;
