/** @format */

import React from "react";
import styled from "styled-components";

const MenuCardBlock = styled.div`
  width: 90%;
  margin: 0 auto;
  display: flex;
  padding: 2vh 0;
  border-bottom: 1px solid #c5c5c3;
`;
const MenuCardDesc = styled.div`
  flex: 2.2;
  padding-right: 1rem;
`;

const MenuCardImg = styled.div`
  flex: 1;
  width: 100px;
  height: 100px;
  background: #f6f690;
`;

const MenuCardDescT = styled.div`
  font-weight: bold;
  font-size: 1rem;
  margin-bottom: 0.2em;
`;

const MenuCardDescP = styled.div`
  font-size: 0.9rem;
  margin-bottom: 0.2em;
`;

const MenuCardDescD = styled.div`
  width: 75%;
  font-size: 0.7rem;
  color: #c5c5c3;
  line-height: 130%;
`;

const MenuCardMark = styled.div`
  width: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid #55c5f9;
  color: #2bb7f8;
  border-radius: 100%;
  padding: 0.5rem;
  font-size: 0.6rem;
  margin-bottom: 0.1rem;
`;
const MenuCard = () => {
  return (
    <MenuCardBlock>
      <MenuCardDesc>
        <MenuCardMark>주문많음</MenuCardMark>
        <MenuCardDescT>순살양념구이</MenuCardDescT>
        <MenuCardDescP>20,000원</MenuCardDescP>
        <MenuCardDescD>
          부드럽고 바삭한 정육 순살에 감칠 맛나는 마늘 간장 소스가 어우러진
          순살치킨
        </MenuCardDescD>
      </MenuCardDesc>
      <MenuCardImg />
    </MenuCardBlock>
  );
};

export default MenuCard;
