/** @format */

import React from "react";
import { Form, Input, Button } from "antd";
import Link from "next/link";
import styled from "styled-components";

const FormBlock = styled(Form)`
  margin: 0 auto;
  max-width: 768px;
  height: 100vh;
  padding: 60px 30px;
`;

const InputBlock = styled(Input)`
  padding: 5px 8px;
  margin-top: 8px;
  border: none;
  outline: none;
  border-bottom: 1px solid blue;
`;

const LoginForm = () => {
  return (
    <FormBlock>
      <h1>규몽이츠</h1>
      <div>
        <InputBlock type="text" name="user-id" placeholder="아이디" />
      </div>
      <div>
        <InputBlock
          type="password"
          name="user-password"
          placeholder="비밀번호"
        />
      </div>
      <Button htmlType="submit">로그인</Button>
      <Link href="/signup">
        <a>
          <Button>회원가입</Button>
        </a>
      </Link>
    </FormBlock>
  );
};

export default LoginForm;
