/** @format */

import React, { useState } from "react";
import Link from "next/link";
import { Menu } from "antd";
import {
  HomeOutlined,
  SearchOutlined,
  HeartOutlined,
  CarryOutOutlined,
  UserOutlined,
} from "@ant-design/icons";
import LoginForm from "./LoginForm";
import styled from "styled-components";

const MenuBlock = styled(Menu)`
  display: flex;
  justify-content: space-between;
  bottom: 0;
  z-index: 100;
`;

// eslint-disable-next-line react/prop-types
const AppLayout = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  return (
    <div>
      {isLoggedIn ? (
        <>
          {children}{" "}
          <Menu mode="inline">
            <Menu.Item>
              <Link href="/">
                <a>
                  <HomeOutlined />
                </a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <Link href="/search">
                <a>
                  <SearchOutlined />
                </a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <Link href="/favorite">
                <a>
                  <HeartOutlined />
                </a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <Link href="/cart">
                <a>
                  <CarryOutOutlined />
                </a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <Link href="/profile">
                <a>
                  <UserOutlined />
                </a>
              </Link>
            </Menu.Item>
          </Menu>
        </>
      ) : (
        <LoginForm setIsLoggedIn={setIsLoggedIn} />
      )}
    </div>
  );
};

export default AppLayout;
